	public void findPage() {
		Page page = new Page();
		
		HashMap proMap = new HashMap();
		proMap.put("name", "%分%");
		page =  service.findByHqlPage(
				"FROM Role  where name like:name",
				proMap, page);
		Assert.assertTrue(page.getRs()!=null);
		Assert.assertTrue(page.getRs().size()>0);
		System.out.println(page.getTotal());
	}
